import React from 'react'

const Filter = (props) => {

  let filterData=props.filterData;
  let platform=props.platform;
  let setPlatform=props.setPlatform;

  function filterHandler(title){
    setPlatform(title); 
  }

  return (
    <div className='w-11/12 flex flex-wrap max-w-max space-x-4 gap-y-4 mx-auto py-4 justify-center'>
        {filterData.map((data)=>{
            return (<button onClick={()=>filterHandler(data.title)} key={data.id} 
            className={`text-lg px-2 py-1 rounded-md font-medium text-white bg-black hover:bg-opacity-50 border-2 transition-all duration-300
            ${(platform===data.site || platform==="All")?"bg-opacity-60 border-white":"bg-opacity-40 border-transparent"}
            `}>
                {data.title}
            </button>)
        })}
    </div> 
  )
}

export default Filter
